mapper.select:
select maker
from car_country
where country=#{ country }

select   model
from   car_maker
where    maker=#{ maker }

select car_year, price, cc, substr(car_option,0,20)||'...' car_option,car_img,
		to_char(hiredate, 'yyyy-mm-dd hh24:mi') hiredate
from(select car_year, price, cc, car_option, car_img,
			hiredate,row_number() over(order by hiredate desc) rnum
	from car_model
	
	
	model=#{ model }
	
	
)


rnum between 1 and 10
